											--raise notice 'parent_ref_var %', parent_ref_var;
											--raise notice 'source_text %', source_text;
											RAISE NOTICE 'INSERT %' , tmp_name;
											INSERT INTO taxonomy (id, name, level_ref, parent_ref,  metadata_ref, import_ref) VALUES (DEFAULT,
