 AND taxonomy.level_ref=staging_catalogue.level_ref AND taxonomy.metadata_ref=metadata_id;
					IF FOUND THEN
						UPDATE staging_catalogue SET import_exception='taxon_already_exists' WHERE id=staging_catalogue_line.id;
