									--UPDATE taxonomy SET source=TRIM(COALESCE(source,'')||'; '||staging_catalogue_line.source_taxonomy) WHERE id=tmp_taxonomy_id::integer;
									is_parent:=FALSE;
									previous_staging_id_as_parent:=staging_catalogue_line.id;
