		END IF;
		IF with_rank = TRUE THEN
			kingdom_name:= kingdom_name||' (kingdom)';
		END If;
		returned :=  kingdom_name||'/'||returned;
		
		IF domain_given = FALSE THEN
				-- 2019 02 19
