    FOR curs IN SELECT staging_gtu.* from staging_gtu WHERE id = p_staging_gtu_id		
    LOOP
    
        raise notice 'test %', curs.sampling_code;
		
					
		SELECT collection_ref INTO collection_id FROM darwin2.imports WHERE id=curs.import_ref;
                expedition :=NULL;
