                        raise notice 'insert %', expedition;
                        INSERT INTO expeditions (name) VALUES (expedition) RETURNING id INTO expedition_id;
                    END IF;
                END IF;
                RAISE NOTICE 'exp_id %', expedition_id;
                expedition_array=expedition_array|| expedition_id;
                
                INSERT INTO gtu(
