                END IF;
                IF curs.coordinates_datum IS NOT NULL THEN
                    INSERT INTO properties( referenced_relation, record_id, property_type, lower_value, upper_value) VALUES ('gtu', gtu_id, 'coordinates_datum', curs.coordinates_datum ,'');
