                END AS decimal_start_latitude,
                CASE
                    WHEN (start_latitude.value IS NULL) THEN NULL::double precision
                    ELSE darwin2.dms2dd(darwin2.cleanupdms((start_longitude.value)::text))
                END AS decimal_start_longitude,
