class Expeditions extends BaseExpeditions
{
  public function getExpeditionFromDateMasked ()
  {
    $dateTime = new FuzzyDateTime($this->_get('expedition_from_date'), $this->_get('expedition_from_date_mask'));
    return $dateTime->getDateMasked();
  }
  
  public function getExpeditionToDateMasked ()
  {
    $dateTime = new FuzzyDateTime($this->_get('expedition_to_date'), $this->_get('expedition_to_date_mask'));
    return $dateTime->getDateMasked();
  }
  
  public function getExpeditionFromDate()
  {
    $from_date = new FuzzyDateTime($this->_get('expedition_from_date'), $this->_get('expedition_from_date_mask'));
    return $from_date->getDateTimeMaskedAsArray();
  }

  public function getExpeditionToDate()
  {
    $to_date = new FuzzyDateTime($this->_get('expedition_to_date'), $this->_get('expedition_to_date_mask'), false);
    return $to_date->getDateTimeMaskedAsArray();
  }

  public function setExpeditionFromDate($fd)
  {
    if ($fd instanceof FuzzyDateTime)
    {
      $this->_set('expedition_from_date', $fd->format('Y/m/d'));
      $this->_set('expedition_from_date_mask', $fd->getMask());
    }
    else
    {
      $dateTime = new FuzzyDateTime($fd, 56, true); 
      $this->_set('expedition_from_date', $dateTime->format('Y/m/d'));
      $this->_set('expedition_from_date_mask', $dateTime->getMask());
    }
  }

  public function setExpeditionToDate($fd)
  {
    if ($fd instanceof FuzzyDateTime)
    {
      $this->_set('expedition_to_date', $fd->format('Y/m/d'));
      $this->_set('expedition_to_date_mask', $fd->getMask());
    }
    else
    {
      $dateTime = new FuzzyDateTime($fd, 56, false); 
      $this->_set('expedition_to_date', $dateTime->format('Y/m/d'));
      $this->_set('expedition_to_date_mask', $dateTime->getMask());
    }
  }

  public function getName()
  {
    if(! $this->isNew() && $this->_get('id')==0)
      return '-';
    return $this->_get('name');
  }
  
   public function countSpecimens()
  {
    $q = Doctrine_Query::create()
      ->select("count(*) as count ")
      ->from('Specimens s')
      ->where('s.expedition_ref = ?', $this->getId());
    $row= $q->fetchOne();
    return $row["count"];
  }
  
    //ftheeten 2018 04 10
  public function countSpecimensByCollections()
  {
    $returned=Array();
    $conn = Doctrine_Manager::connection();
    $tmpId=$this->getId() ;
	$sql = "select collection_name, COUNT(id) as count FROM specimens WHERE expedition_ref=$tmpId GROUP BY collection_name ORDEr BY collection_name;";
	//$q = $conn->prepare($sql);
	$rows=$conn->execute($sql)->fetchAll();    
    return $rows;
  }
  
  //ftheeten 2018 04 10
  public function countSpecimensByCollectionsString()
  {
    $returned=Array();
    $array=$this->countSpecimensByCollections();
    if(count($array)>0)
    {
        foreach($array as $row)
        {
            $returned[]=$row["collection_name"].": ".$row["count"];
        }
        return implode("; ",$returned);
    }
    else
    {
        return "";
    }
    
  }

