  public function getName()
  {
    $name = '-';
    if(! $this->isNew() && $this->_get('id')==0)
      return $name;
    $codes = Doctrine_Core::getTable('Codes')->getCodesRelated('specimens', $this->_get('id'));
    if (!$codes->count())
      return $name;
    $name = '';
    foreach ($codes as $code)
    {
      if ($code->getCodeCategory() == 'main') {
        $name = '['.$code->getCodeFormated().'] ';
        break;
      }
      else {
        $name .= '['.$code->getCodeFormated().'] ';
      }
    }
    $name = rtrim($name);
    return $name;
  }
  
