					GROUP BY name_indexed , level_ref HAVING count(*) > 1
			LOOP 
				duplicates:=duplicates||cursor_tmp.name;
				i:=i+1;

			END LOOP;
	
	
			IF i>0 THEN
				RAISE EXCEPTION 'Some taxa are present several times in the reference taxonomy, check %', array_to_string(duplicates,',');
			END IF;
		END IF;
   END IF;

	    
  RETURN NEW;
END;
$$;


