							--SUBSTRING(array_ids_string FROM  POSITION ('/'||last_id||'/' IN array_ids_string) + LENGTH('/'||last_id));
						ELSE
							--RAISE NOTICE 'EXIT 1';
							RETURN returned;
						END IF;
					ELSE 
						--RAISE NOTICE 'EXIT 2';
						RETURN returned;
					END IF;
				END IF;
				
			END LOOP;
		END IF;
--RAISE NOTICE 'EXIT 3';
	return returned;
END;

$$;


