					level_tmp_char:=staging_path_level_array[i];
					--taxonomic parent already in staging
					IF ARRAY_LENGTH( parent_updated_refs_array ,1)> 0 AND i < ARRAY_LENGTH( parent_updated_refs_array ,1) THEN
						
