					END IF;
					IF id_main_taxon IS NULL THEN
						--raise notice 'NOT_FOUND';
						returned_tmp:= returned_tmp||'NOT_FOUND/';
						returned_tmp_levels:= returned_tmp_levels||'NOT_FOUND/';
