        if ARRAY_LENGTH(acc,1)>0 THEN
		returned:=fct_cast_to_real(acc[1]);
		
		IF LOWER(direction)='latitude' THEN
			IF POSITION('S' IN  UPPER(coord_dms))>0 THEN
				returned=returned*-1;
			END IF;
		ELSEIF LOWER(direction)='longitude' THEN
