  GROUP BY t1.gtu_ref, t1.tag_group_distinct_ref, t1.tag_value, tacountry.code, tacountry.url, tacountry.pref_label, (tacountry.alternative_representations OPERATOR(public.->) 'ISO 3166-2'::text)
