    LANGUAGE plpgsql
    AS $$
BEGIN
  IF TG_OP ='INSERT' THEN
    NEW.parent_ref_internal=NEW.parent_ref;
  
  ELSIF TG_OP ='UPDATE' AND NEW.parent_updated=FALSE  THEN
    NEW.parent_ref_internal=NEW.parent_ref;  
    
  END IF;
  RETURN NEW;
END;
$$;


