                 INNER JOIN
                (
                       SELECT distinct unnest(string_to_array(taxon_path||'/'||taxon_ref::varchar, '/'))  as key_taxon from specimens where  collection_ref =$1 OR collection_path LIKE '%/'||$1||'/%'
                       
                      
