CREATE VIEW darwin2.v_rmca_public_view AS
 SELECT DISTINCT specimens.id,
    specimens.code_display,
    specimens.taxon_path,
    specimens.taxon_ref,
    specimens.taxon_name,
    specimens.gtu_country_tag_value,
